// This function extracts text content from the Google Drive file
let fileContent = $input.item.json.fileContent;
let fileType = $input.item.json.mimeType;

// Handle different document types
let extractedText = '';

if (fileType === 'application/pdf') {
  // For PDFs, use the binary data and extract text
  // This is simplified; in a real workflow you might use a specific PDF parser
  extractedText = fileContent.toString('utf-8');
} else if (fileType === 'application/vnd.google-apps.document') {
  // For Google Docs, the content might already be in HTML or plain text format
  extractedText = fileContent;
} else if (fileType.includes('text/') || fileType.includes('application/json')) {
  // For text files or JSON
  extractedText = fileContent;
} else {
  // For other file types, you might need specific parsers
  throw new Error(`Unsupported file type: ${fileType}`);
}

return {
  documentName: $input.item.json.name,
  documentId: $input.item.json.id,
  extractedText: extractedText,
  sectionMarkers: $workflow.input.sectionMarkers,
  chunkSize: $workflow.input.chunkSize,
  chunkOverlap: $workflow.input.chunkOverlap
};

