// Function to create context-aware chunks from the document
const text = items[0].json.extractedText;
const documentName = items[0].json.documentName;
const documentId = items[0].json.documentId;
const sectionMarker = items[0].json.sectionMarkers;
const maxChunkSize = items[0].json.chunkSize;
const chunkOverlap = items[0].json.chunkOverlap;

// Split the document into sections based on markers
let sections = [];
if (text.includes(sectionMarker)) {
  sections = text.split(sectionMarker).filter(section => section.trim() !== '');
} else {
  // If no section markers found, treat the whole document as one section
  sections = [text];
}

// Function to split text into chunks with overlap
function createChunks(text, maxSize, overlap) {
  const chunks = [];

  if (text.length <= maxSize) {
    chunks.push(text);
    return chunks;
  }

  let startIndex = 0;
  while (startIndex < text.length) {
    // Find a good breaking point - preferably at a paragraph or sentence end
    let endIndex = Math.min(startIndex + maxSize, text.length);

    // Try to find paragraph break
    const paragraphBreak = text.lastIndexOf('\n\n', endIndex);
    if (paragraphBreak > startIndex && paragraphBreak > endIndex - maxSize/2) {
      endIndex = paragraphBreak;
    } else {
      // Try to find sentence break
      const sentenceBreak = text.lastIndexOf('. ', endIndex);
      if (sentenceBreak > startIndex && sentenceBreak > endIndex - maxSize/3) {
        endIndex = sentenceBreak + 1; // Include the period
      }
    }

    chunks.push(text.substring(startIndex, endIndex).trim());
    startIndex = endIndex - overlap;
  }

  return chunks;
}

// Process each section and create context-aware chunks
const contextAwareChunks = [];
let sectionIndex = 0;

sections.forEach((sectionText, index) => {
  // Extract a section title (first line or first few words)
  let sectionTitle = '';
  const firstLineBreak = sectionText.indexOf('\n');
  if (firstLineBreak > 0) {
    sectionTitle = sectionText.substring(0, firstLineBreak).trim();
  } else {
    // If no line break, use the first 50 characters or less
    sectionTitle = sectionText.substring(0, Math.min(50, sectionText.length)).trim();
    if (sectionTitle.length === 50) sectionTitle += '...';
  }

  // Create chunks for this section
  const sectionChunks = createChunks(sectionText, maxChunkSize, chunkOverlap);

  // Add context metadata to each chunk
  sectionChunks.forEach((chunk, chunkIndex) => {
    contextAwareChunks.push({
      chunk,
      metadata: {
        documentId,
        documentName,
        sectionIndex: index + 1,
        sectionTitle,
        chunkIndex: chunkIndex + 1,
        totalChunksInSection: sectionChunks.length,
        totalSections: sections.length,
        chunkPosition: `Section ${index + 1}, Chunk ${chunkIndex + 1} of ${sectionChunks.length}`
      }
    });
  });
});

// Return array of chunks for processing
return contextAwareChunks.map(item => {
  return {
    json: item
  };
});

