  metadata: res.payload.metadata
}));
const retriever = BM25Retriever.fromDocuments(docs, { k: limit });
const rankedDocs = await retriever.invoke(query);
return rankedDocs;

const { randomUUID } = require('crypto') // Enable the crypto lib using env var NODE_FUNCTION_ALLOW_BUILTIN=crypto
const { QdrantClient } = require('@qdrant/js-client-rest');

// 1. Qdrant config
const client = new QdrantClient({ url: 'http://qdrant:6333' });
const collectionName = 'contextual_retrieval_example';

// 2. Inputs
const inputData = this.getInputData();
const embeddings = await this.getInputConnectionData('ai_embedding', 0);
const documentLoader = await this.getInputConnectionData('ai_document', 0);

// 3. Run document loader
const docs = await documentLoader.processAll(inputData);

// 4. generate points with sparse vectors
const points = [];
let vector = {};
for (let i=0,j=docs.length;i<j;i++) {
  points.push({
    id: randomUUID(),
    vector: {
      default: await embeddings.embedQuery(docs[i].pageContent),
      bm42: inputData[i].json.sparse,
    },
    payload: {
      content: docs[i].pageContent,
      metadata: docs[i].metadata,
    }
  })
}

// 5. Delete existing vectors by title
await client.delete(collectionName, {
  filter: {
    must: [
      {
        key: "metadata.title",
        match: { "value": docs[0].metadata.title }
      }
    ]
  }
});

// 6. Upsert into Qdrant
const res = await client.upsert(collectionName, { points });

return res;

