// Example usage
const input = $input.all();
console.log(input);
const transformedSchema = transformSchema(input);

return { data: transformedSchema };

// Initialize the output arrays
const steps = [];
const tokens = [];
let totalTokens = 0; // Changed to track total tokens instead of cost

// Process each item in the input
for (const item of $input.all()) {
  let data = item.json;

  // Check if the data is an array itself (like your example JSON)
  if (Array.isArray(data)) {
    // Process each object in the array
    for (const obj of data) {
      // Extract steps information
      if (obj.intermediateSteps && Array.isArray(obj.intermediateSteps)) {
        for (const step of obj.intermediateSteps) {
          if (step.action) {
            steps.push({
              tool: step.action.tool,
              toolInput: step.action.toolInput,
              observation: step.observation
            });
          }
        }
      }

      // Extract token information
      if (obj.intermediateSteps && Array.isArray(obj.intermediateSteps)) {
        for (const step of obj.intermediateSteps) {
          if (step.action && step.action.messageLog && Array.isArray(step.action.messageLog)) {
            for (const message of step.action.messageLog) {
              if (message.kwargs && message.kwargs.response_metadata && message.kwargs.response_metadata.usage) {
                const usage = message.kwargs.response_metadata.usage;
                tokens.push({
                  prompt_tokens: usage.prompt_tokens,
                  completion_tokens: usage.completion_tokens,
                  total_tokens: usage.total_tokens,
                  model_name: message.kwargs.response_metadata.model_name
                });

                // Add the tokens to our running total
                if (typeof usage.total_tokens === 'number') {
                  totalTokens += usage.total_tokens;
                }
              }
            }
          }
        }
      }
    }
  } else {
    // If data is not an array, process it as a single object
    // Extract steps information
    if (data.intermediateSteps && Array.isArray(data.intermediateSteps)) {
      for (const step of data.intermediateSteps) {
        if (step.action) {
          steps.push({
            tool: step.action.tool,
            toolInput: step.action.toolInput,
            observation: step.observation
          });
        }
      }
    }

    // Extract token information
    if (data.intermediateSteps && Array.isArray(data.intermediateSteps)) {
      for (const step of data.intermediateSteps) {
        if (step.action && step.action.messageLog && Array.isArray(step.action.messageLog)) {
          for (const message of step.action.messageLog) {
            if (message.kwargs && message.kwargs.response_metadata && message.kwargs.response_metadata.usage) {
              const usage = message.kwargs.response_metadata.usage;
              tokens.push({
                prompt_tokens: usage.prompt_tokens,
                completion_tokens: usage.completion_tokens,
                total_tokens: usage.total_tokens,
                model_name: message.kwargs.response_metadata.model_name
              });

              // Add the tokens to our running total
              if (typeof usage.total_tokens === 'number') {
                totalTokens += usage.total_tokens;
              }
            }
          }
        }
      }
    }
  }
}

// Return the processed data with total_tokens included
return [{
  json: {
    steps: steps,
    tokens: tokens,
    total_tokens: totalTokens
  }
}];

// n8n Code Node - Split Golf Rules
// This code takes the input text and splits it into separate items for each rule

// Get the input text from the first item
const inputText = $input.first().json.text;

// Split the text by "Rule" pattern, keeping the "Rule" text with each section
const ruleSections = inputText.split(/(?=Rule \d+)/);

// Remove the first empty element (everything before the first "Rule")
const cleanedSections = ruleSections.filter(section => section.trim().startsWith('Rule'));

// Create output items - one for each rule
const outputItems = cleanedSections.map((ruleText, index) => {
  // Extract rule number from the text
  const ruleMatch = ruleText.match(/Rule (\d+)/);
  const ruleNumber = ruleMatch ? ruleMatch[1] : (index + 1).toString();

  // Extract rule title (everything between "Rule X â€“" and the first numbered item)
  const titleMatch = ruleText.match(/Rule \d+ â€“ (.+?)(?=\n1\.|\n\d+\.)/);
  const ruleTitle = titleMatch ? titleMatch[1].trim() : 'Unknown Rule';

  return {
    json: {
      ruleNumber: ruleNumber,
      ruleTitle: ruleTitle,
      fullText: ruleText.trim(),
      originalIndex: index
    }
  };
});

return outputItems;
